mapper.select:
SELECT uids,title, insert_ts, 'freeboard' as board_name, fid as bno FROM free_board a WHERE uids = #{uids}
UNION ALL
SELECT uids,title, insert_ts, 'qna' as board_name, qid as bno FROM qna_board a WHERE uids = #{uids}

SELECT uids,content, insert_ts FROM fb_comment WHERE uids = #{uids}
UNION ALL
SELECT uids,content, insert_ts FROM plan_comment WHERE uids = #{uids}

mapper.update:
UPDATE checklist
SET item = #{item},
	memo = #{memo}
WHERE
    cid = #{cid}
