mapper.select:
SELECT uids,title, insert_ts, 'freeboard' as board_name, fid as bno, 'fid' as bm FROM free_board a WHERE uids = #{uids}
UNION ALL
SELECT uids,title, insert_ts, 'qna' as board_name, qid as bno, 'qid' as bm FROM qna_board a WHERE uids = #{uids}

SELECT uids,content, insert_ts, 'freeboard' as board_name, fid as bno, 'fid' as bm FROM fb_comment a WHERE uids = #{uids}
UNION ALL
SELECT uids,content, insert_ts, 'plan_board' as board_name, pid as bno, 'pid' as bm FROM plan_comment a WHERE uids = #{uids}

mapper.update:
UPDATE checklist
SET checkbox = #{checkbox},
	item = #{item},
	memo = #{memo}
WHERE
    cid = #{cid}

mapper.select:
SELECT fid, title, 'freeboard' as board_name, fid as bno, 'fid' as bm FROM fb_bookmark WHERE uids = #{uids}
UNION ALL
SELECT pid, title, 'plan_bookmark' as board_name, pid as bno, 'pid' as bm FROM plan_bookmark WHERE uids = #{uids}
