mapper.select:
SELECT uids,title, insert_ts FROM free_board WHERE uids = #{uids}
UNION ALL
SELECT uids,title, insert_ts FROM qna_board WHERE uids = #{uids}

SELECT uids,content, insert_ts FROM fb_comment WHERE uids = #{uids}
UNION ALL
SELECT uids,content, insert_ts FROM plan_comment WHERE uids = #{uids}

mapper.update:
UPDATE checklist
SET item = #{item},
	memo = #{memo}
WHERE
    cid = #{cid}
