mapper.select:
SELECT pid, uids, planTitle, startDate, planTotalDay, regDate 
 FROM plan_board 
  WHERE uids = #{uids} AND pid = #{pid}

mapper.update:
UPDATE plan_board
SET	planTitle = #{planTitle}, startDate = #{startDate} 
WHERE uids = #{uids} AND pid = #{pid}

mapper.delete:
DELETE
FROM plan_board
WHERE pid = #{pid} AND uids = #{uids};

DELETE 
FROM schedule
WHERE pid = #{pid} AND rowNo = #{rowNo};
